import PropTypes from 'prop-types';
import React, { Component } from 'react';
import '../styles/albumThumbnail.css'


class AlbumThumbnail extends Component {
  render() {
    const {
      artist,
      collectionName,
      price,
      img,
      releaseDate,
    } = this.props;

    return (

    <section className="album-card">
      <div className='album-mage'>
        <img src={ img } alt={ `Cover From ${collectionName}` } />
        </div>
        <h7 data-testid="artist-name" className="artist-info">{artist}</h7>
        <p data-testid="album-name">{collectionName}</p>
        <p>{`Price: $${price}`}</p>
        <p>{`Released Date: ${new Date(releaseDate).toLocaleDateString('en')}`}</p>
    </section>

    );
  }
}

AlbumThumbnail.propTypes = {
  artist: PropTypes.string.isRequired,
  collectionName: PropTypes.string.isRequired,
  price: PropTypes.number.isRequired,
  img: PropTypes.string.isRequired,
  releaseDate: PropTypes.string.isRequired,
};

export default AlbumThumbnail;
