import React, { Component } from 'react';

class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      enableButton: true,
    };

    this.btn1.bind(this);
  }

  btn1 = (event) => {
    const valor = event.target.value.length;
    const min = 3;
    if (valor >= min) {
      this.setState({
        enableButton: false,
      });
    }
  }

  render() {
    const { enableButton } = this.state;
    return (
      <div data-testid="page-login">
        <form>
          <input
            type="text"
            placeholder="nome de usuario"
            data-testid="login-name-input"
            onChange={ this.btn1 }
            name="name"
          />
          <button
            type="submit"
            disabled={ enableButton }
            data-testid="login-submit-button"
          >
            Entrar

          </button>
        </form>
      </div>
    );
  }
}

export default Login;
