import React, { Component } from 'react';
import Header from '../Components/Header';
import Loading from '../Components/Loading';
import MusicCard from '../Components/MusicCard';
import getMusic from '../services/musicsAPI';
import { addSong, removeSong, getFavoriteSongs } from '../services/favoriteSongsAPI';

class Album extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading: false,
      musics: [],
      favsongs: [],
    };
    this.toLoad = this.toLoad.bind(this);
  }

  componentDidMount() {
    const { params } = this.props;
    const { id } = params;
    const { favsongs } = this.state;
    const { musics } = this.state;
    getMusic(id)
      .then((response) => this.setState({ musics: [...response] }))
      .catch((error) => console.log(error));
    getFavoriteSongs()
      .then((fav) => this.setState({ favsongs: [...fav] }));
  }

  componentWillUnmount() {
    this.setState({
      favsongs: [],
      musics: [],
    });
  }

  toLoad() {
    this.setState((state) => ({ loading: !state.loading }));
  }

  render() {
    const { musics, loading, favsongs } = this.state;
    console.log(favsongs);
    const artistName = musics.map((track) => track.artistName);
    const albumName = musics.map((track) => track.collectionName);
    const musicList = musics.slice(1).map((track, key) => (
      <MusicCard
        key={ key }
        number={ track.trackNumber }
        musicName={ track.trackName }
        previewUrl={ track.previewUrl }
        musicId={ track.trackId }
        trackObj={ track }
        toLoad={ this.toLoad }
        loading={ loading }
        checked={ favsongs.some((song) => song.musicId === track.musicId) }

      />
    ));
    return (
      <div data-testid="page-album">
        { loading ? <Loading /> : (
          <>
            <Header />
            <div data-testid="artist-name">
              {'Artist:  '}
              { artistName[0]}
            </div>
            <div data-testid="album-name">
              {'Album:  '}
              {albumName[0]}
            </div>
            { musicList }
          </>
        )}
      </div>
    );
  }
}

export default Album;
