import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { addSong, removeSong } from '../services/favoriteSongsAPI';
import Loading from './Loading';
import { getFavoriteSongs } from '../services/favoriteSongsAPI';

class MusicCard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      checkSaved: [],
    };
    this.favSong = this.favSong.bind(this);
  }

  componentDidMount() {
    this.getFavoritesData();
  }

  getFavoritesData = async () => {
    const { checkSaved } = this.state
    this.setState({ checkSaved: await getFavoriteSongs() });
    console.log(checkSaved);
  }

  async favSong(target) {
    const { checked } = target;
    const { toLoad, musicId, toaddToFavState } = this.props;
    const { ...track } = this.props;
    const objToPass = { ...track };
    toLoad();
    await addSong(objToPass);
    toLoad();
  }

  render() {
    const {
      number,
      musicName,
      previewUrl,
      musicId,
      loading,
    } = this.props;

    return (
      <div>
        <div>
          { `${number} - ${musicName}` }
        </div>
        <audio data-testid="audio-component" src={ previewUrl } controls>
          <track kind="captions" />
          O seu navegador não suporta o elemento
          <code>audio</code>
        </audio>
        <label htmlFor="fav">
          Favorita
          <input
            data-testid={ `checkbox-music-${musicId}` }
            type="checkbox"
            name="fav"
            onChange={ this.favSong }
          />
        </label>

      </div>
    );
  }
}

MusicCard.propTypes = {
  number: PropTypes.string.isRequired,
  musicName: PropTypes.string.isRequired,
  previewUrl: PropTypes.string.isRequired,
  musicId: PropTypes.string.isRequired,
};
export default MusicCard;
